mapper.update:
UPDATE members
SET username = #{username},
    email = #{email},
    password = #{password},
    phone = #{phone}
WHERE id = #{id}

mapper.select:
SELECT id, username, email, password, phone, created_at
FROM members
WHERE email = #{email} AND password = #{password}

SELECT id, username, email, password, phone, created_at as createdAt
FROM members
WHERE email = #{email}
